import "./App.css";
import React, { Component } from "react";

import axios from "axios";

class GamesList extends Component {
  constructor() {
    super();
    this.state = {
      socapi: [],
      isLoading: true
    };
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/socapi`).then(response => {
      const socapi = response.data;
      this.setState({
        socapi,
        isLoading: false
      });
    });
  }

  render() {
    const { socapi } = this.state;
    if (this.state.isLoading) {
      return <div>Loading . . . </div>;
    }
    return (
      <div>
        <ul>{socapi && socapi.map(soc => <li>{soc.id}</li>)}</ul>
      </div>
    );
  }
}
export default GamesList;
