import "./App.css";
import React, { Component } from "react";

import axios from "axios";

class GamesList extends Component {
  constructor() {
    super();
    this.state = {
      socapi: [],
      isLoading: true
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });
    axios
      .get(`http://localhost:8000/socapi`, { withCredentials: true })
      .then(response => {
        const socapi = response.data;
        this.setState({
          socapi,
          isLoading: false
        });
      });
  }

  render() {
    const { socapi } = this.state;
    if (this.state.isLoading) {
      return <div>Loaading...</div>;
    }
    return (
      <ul>
        {socapi.map(soc => (
          <li>{soc.id}</li>
        ))}
      </ul>
    );
  }
}
export default GamesList;
